import React, { useState } from 'react';
import axios from '../utils/axios';

const AdCard = ({ ad, onRegenerate, onDownload }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('preview');

  const {
    headline,
    subheadline,
    cta,
    imageUrl,
    format,
    style: { colors = ['#007bff', '#ffffff'], tone = 'professional' } = {}
  } = ad;

  // Ensure colors is always an array
  const colorArray = Array.isArray(colors) ? colors : [colors, '#ffffff'];

  // Handle regenerate specific elements
  const handleRegenerateElement = (element) => {
    onRegenerate(ad, element);
  };

  // Handle download
  const handleDownload = (type) => {
    if (isLoading) return;

    setIsLoading(true);
    try {
      onDownload(ad, type);
    } catch (error) {
      console.error('Error downloading ad:', error);
      alert('Failed to download. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  // Handle CTA click
  const handleCtaClick = () => {
    // Open a modal or navigate to a details page
    alert(`This would take the user to a landing page for: ${headline}`);
  };

  return (
    <div className="bg-white rounded-lg shadow-lg overflow-hidden transition-transform duration-300 hover:transform hover:scale-102">
      {/* Ad Preview */}
      <div className="relative" style={{ backgroundColor: colorArray[0] }}>
        {imageUrl && (
          <div className="relative">
            <img 
              src={imageUrl.startsWith('http') ? imageUrl : `${axios.defaults.baseURL}${imageUrl}`} 
              alt={headline} 
              className="w-full object-cover"
              style={{ maxHeight: '300px' }}
            />
            <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/70 to-transparent p-4">
              <h3 className="text-white text-xl font-bold mb-2">{headline}</h3>
              <p className="text-white text-sm mb-4">{subheadline}</p>
              <button 
                className="px-4 py-2 rounded-md font-medium text-sm"
                style={{ backgroundColor: colorArray[2], color: colorArray[0] }}
                onClick={handleCtaClick}
              >
                {cta}
              </button>
            </div>
          </div>
        )}

        {!imageUrl && (
          <div className="p-6 text-center">
            <h3 className="text-xl font-bold mb-2" style={{ color: colorArray[1] }}>{headline}</h3>
            <p className="text-sm mb-4" style={{ color: colorArray[1] }}>{subheadline}</p>
            <button 
              className="px-4 py-2 rounded-md font-medium text-sm"
              style={{ backgroundColor: colorArray[2], color: colorArray[0] }}
              onClick={handleCtaClick}
            >
              {cta}
            </button>
          </div>
        )}
      </div>

      {/* Tabs */}
      <div className="border-b border-gray-200">
        <nav className="flex -mb-px">
          <button
            onClick={() => setActiveTab('preview')}
            className={`py-2 px-4 text-sm font-medium ${
              activeTab === 'preview'
                ? 'border-b-2 border-blue-500 text-blue-600'
                : 'text-gray-500 hover:text-gray-700'
            }`}
          >
            Preview
          </button>
          <button
            onClick={() => handleRegenerateElement('image')}
            className="bg-white text-gray-800 px-3 py-1 rounded-full text-sm font-medium"
          >
            Regenerate Image
          </button>
        </nav>
        <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent">
          <div className="absolute bottom-0 left-0 right-0 p-4 text-white">
            <p className="text-xs uppercase tracking-wide mb-1">
              Format: {format}
            </p>
            <p className="text-xs uppercase tracking-wide mb-1">
              Tone: {tone}
            </p>
          </div>
        </div>
      </div>

      {/* Ad Content */}
      <div className="p-6">
        <div className="group relative">
          <h3 className="text-xl font-bold mb-2 text-gray-900">
            {headline}
          </h3>
          <div className="absolute right-0 top-0 opacity-0 group-hover:opacity-100 transition-opacity">
            <button 
              onClick={() => handleRegenerateElement('headline')}
              className="text-xs text-blue-600 hover:text-blue-800"
            >
              Regenerate
            </button>
          </div>
        </div>

        <div className="group relative">
          <p className="text-gray-600 mb-4">
            {subheadline}
          </p>
          <div className="absolute right-0 top-0 opacity-0 group-hover:opacity-100 transition-opacity">
            <button 
              onClick={() => handleRegenerateElement('subheadline')}
              className="text-xs text-blue-600 hover:text-blue-800"
            >
              Regenerate
            </button>
          </div>
        </div>

        <div className="group relative">
          <button
            className="w-full mb-3 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
            style={{ backgroundColor: colorArray[0] }}
          >
            {cta}
          </button>
          <div className="absolute right-0 top-0 opacity-0 group-hover:opacity-100 transition-opacity">
            <button 
              onClick={() => handleRegenerateElement('cta')}
              className="text-xs text-blue-600 hover:text-blue-800"
            >
              Regenerate
            </button>
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="px-6 pb-6 flex space-x-4">
        <button
          onClick={() => handleRegenerateElement('all')}
          disabled={isLoading}
          className={`flex-1 px-4 py-2 border border-gray-300 rounded text-gray-700 hover:bg-gray-50 transition-colors ${isLoading ? 'opacity-50 cursor-not-allowed' : ''}`}
        >
          {isLoading ? 'Regenerating...' : 'Regenerate All'}
        </button>
        <button
          onClick={() => handleDownload('pdf')}
          className="flex-1 px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 transition-colors"
        >
          Download PDF
        </button>
        <button
          onClick={() => handleDownload('ppt')}
          className="flex-1 px-4 py-2 bg-orange-600 text-white rounded hover:bg-orange-700 transition-colors"
        >
          Download PPT
        </button>
        <button
          onClick={() => handleDownload('image')}
          className="flex-1 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition-colors"
        >
          Download Image
        </button>
      </div>

      {/* Color Palette */}
      <div className="px-6 pb-4">
        <div className="flex items-center space-x-2">
          <span className="text-xs text-gray-500">Colors:</span>
          <div className="flex space-x-2">
            {colorArray.map((color, index) => (
              <div
                key={index}
                className="w-6 h-6 rounded-full cursor-pointer hover:ring-2 hover:ring-offset-1 hover:ring-blue-500"
                style={{ backgroundColor: color }}
                title={color}
                onClick={() => handleRegenerateElement('colors')}
              />
            ))}
          </div>
          <button 
            onClick={() => handleRegenerateElement('colors')}
            className="text-xs text-blue-600 hover:text-blue-800 ml-auto"
          >
            Regenerate Colors
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdCard;
